mapper.select:
select
id, member_id, coupon_id, order_sn, create_time, member_username, total_amount, pay_amount, freight_amount,
promotion_amount, integration_amount, coupon_amount, discount_amount, pay_type, source_type, status, order_type,
delivery_company, delivery_sn, auto_confirm_day, integration, growth, promotion_info, bill_type, bill_header,
bill_content, bill_receiver_phone, bill_receiver_email, receiver_name, receiver_phone, receiver_post_code,
receiver_province, receiver_city, receiver_region, receiver_detail_address, note, confirm_status, delete_status,
use_integration, payment_time, delivery_time, receive_time, comment_time, modify_time
from oms_order


        and id = #{omsOrder.id}


        and member_id = #{omsOrder.memberId}


        and coupon_id = #{omsOrder.couponId}


        and order_sn = #{omsOrder.orderSn}


        and create_time = #{omsOrder.createTime}


        and member_username = #{omsOrder.memberUsername}


        and total_amount = #{omsOrder.totalAmount}


        and pay_amount = #{omsOrder.payAmount}


        and freight_amount = #{omsOrder.freightAmount}


        and promotion_amount = #{omsOrder.promotionAmount}


        and integration_amount = #{omsOrder.integrationAmount}


        and coupon_amount = #{omsOrder.couponAmount}


        and discount_amount = #{omsOrder.discountAmount}


        and pay_type = #{omsOrder.payType}


        and source_type = #{omsOrder.sourceType}


        and status = #{omsOrder.status}


        and order_type = #{omsOrder.orderType}


        and delivery_company = #{omsOrder.deliveryCompany}


        and delivery_sn = #{omsOrder.deliverySn}


        and auto_confirm_day = #{omsOrder.autoConfirmDay}


        and integration = #{omsOrder.integration}


        and growth = #{omsOrder.growth}


        and promotion_info = #{omsOrder.promotionInfo}


        and bill_type = #{omsOrder.billType}


        and bill_header = #{omsOrder.billHeader}


        and bill_content = #{omsOrder.billContent}


        and bill_receiver_phone = #{omsOrder.billReceiverPhone}


        and bill_receiver_email = #{omsOrder.billReceiverEmail}


        and receiver_name = #{omsOrder.receiverName}


        and receiver_phone = #{omsOrder.receiverPhone}


        and receiver_post_code = #{omsOrder.receiverPostCode}


        and receiver_province = #{omsOrder.receiverProvince}


        and receiver_city = #{omsOrder.receiverCity}


        and receiver_region = #{omsOrder.receiverRegion}


        and receiver_detail_address = #{omsOrder.receiverDetailAddress}


        and note = #{omsOrder.note}


        and confirm_status = #{omsOrder.confirmStatus}


        and delete_status = #{omsOrder.deleteStatus}


        and use_integration = #{omsOrder.useIntegration}


        and payment_time = #{omsOrder.paymentTime}


        and delivery_time = #{omsOrder.deliveryTime}


        and receive_time = #{omsOrder.receiveTime}


        and comment_time = #{omsOrder.commentTime}


        and modify_time = #{omsOrder.modifyTime}


limit #{pageable.offset}, #{pageable.pageSize}

select count(1)
from oms_order


        and id = #{id}


        and member_id = #{memberId}


        and coupon_id = #{couponId}


        and order_sn = #{orderSn}


        and create_time = #{createTime}


        and member_username = #{memberUsername}


        and total_amount = #{totalAmount}


        and pay_amount = #{payAmount}


        and freight_amount = #{freightAmount}


        and promotion_amount = #{promotionAmount}


        and integration_amount = #{integrationAmount}


        and coupon_amount = #{couponAmount}


        and discount_amount = #{discountAmount}


        and pay_type = #{payType}


        and source_type = #{sourceType}


        and status = #{status}


        and order_type = #{orderType}


        and delivery_company = #{deliveryCompany}


        and delivery_sn = #{deliverySn}


        and auto_confirm_day = #{autoConfirmDay}


        and integration = #{integration}


        and growth = #{growth}


        and promotion_info = #{promotionInfo}


        and bill_type = #{billType}


        and bill_header = #{billHeader}


        and bill_content = #{billContent}


        and bill_receiver_phone = #{billReceiverPhone}


        and bill_receiver_email = #{billReceiverEmail}


        and receiver_name = #{receiverName}


        and receiver_phone = #{receiverPhone}


        and receiver_post_code = #{receiverPostCode}


        and receiver_province = #{receiverProvince}


        and receiver_city = #{receiverCity}


        and receiver_region = #{receiverRegion}


        and receiver_detail_address = #{receiverDetailAddress}


        and note = #{note}


        and confirm_status = #{confirmStatus}


        and delete_status = #{deleteStatus}


        and use_integration = #{useIntegration}


        and payment_time = #{paymentTime}


        and delivery_time = #{deliveryTime}


        and receive_time = #{receiveTime}


        and comment_time = #{commentTime}


